$(function(){$(document).ready(function(){getQuote();}); function getRandomInt(min, max){return Math.floor(Math.random() * (max - min) + min);}function getQuote(){var quoteRequest=$.ajax({type: "GET", url: "https://www.reddit.com/r/quotes/hot.json", dataType: 'json'}); quoteRequest.done(function(response){var data=response.data.children; var min=0; var max=data.length; var i=getRandomInt(min, max); var quote=data[i].data.title; $('p.quote').html(quote);}); quoteRequest.fail(function(jqXHR, textStatus){console.log("Request failed: " + textStatus);});}});